package com.example.pashko00710.testparser.rest;import com.example.pashko00710.testparser.rest.api.PeoplesApi;import com.example.pashko00710.testparser.utils.ConstantsManager;import retrofit.RestAdapter;public class RestClient {    private PeoplesApi peoplesApi;    public RestClient() {        RestAdapter restAdapter = new RestAdapter.Builder()        .setEndpoint(ConstantsManager.BASE_URL)        .setLogLevel(RestAdapter.LogLevel.FULL)        .build();        peoplesApi = restAdapter.create(PeoplesApi.class);    }    public PeoplesApi getPeoplesApi() {        return peoplesApi;    }}