package com.example.pashko00710.testparser.rest.api;import com.example.pashko00710.testparser.rest.model.ImeiModel;import com.example.pashko00710.testparser.rest.model.PeoplesModel;import java.util.ArrayList;import retrofit.http.Body;import retrofit.http.GET;import retrofit.http.POST;import retrofit.http.Path;public interface PeoplesApi {    @GET("/users.json")    public ArrayList<PeoplesModel> getPeoples();    @GET("/users/{id}.json")    public PeoplesModel getItemPeople(@Path("id") String id);    @POST("/uploads")    public ImeiModel sendImei(@Body String upload);}